mapper.select:
select class_id as "classId", org_name as "orgName", class_name as "className", created_date as "createdDate", class_hours as "classHours", class_start_date as "classStartDate", class_end_date as "classEndDate" from class
org_name = #{orgName}
and class_name like #{className}

mapper.update:
update section 
set section_title = #{sectionTitle}
where section_id = #{sectionId}
